signal = ''
#signal = 'mjqjpqmgbljsphdztnvjfqwrcgsmlb'
messagelength = len(signal) - 3

def findMarker():
    for i in range(0,messagelength):
        string = signal[int(i):int(i)+4]
        if len(set(string)) == 4:
            return(i + 4)

def findMessage():
    for i in range(0,messagelength):
        string = signal[int(i):int(i)+14]
        if len(set(string)) == 14:
            return(i + 14)

print(findMessage())
